/*
 * Behavior for the automatic file upload
 */

(function ($) {

  Drupal.behaviors.peoplesCollapse = {
    attach: function (context, settings) {
      $('.m-accordion__item-head').once('peoplesCollapse', function () {
        if ($(this).hasClass('m-accordion__item-expanded')) {
          $(this).next('.m-accordion__item-body').slideDown();
        }
        $(this).on('click', function () {
          $(this).next('.m-accordion__item-body').slideToggle();
          $(this).toggleClass('m-accordion__item-expanded');
        });
      });
    }
  };

  Drupal.behaviors.autoUpload = {
    attach: function(context, settings) {
      $('.form-item input.form-submit[value=' + Drupal.t('Upload') + ']', context).hide();
      $('.form-item input.form-file', context).change(function() {
        var $parent = $(this).closest('.form-item');

        //setTimeout to allow for validation
        //would prefer an event, but there isn't one
        setTimeout(function() {
          if(!$('.error', $parent).length) {
            $('input.form-submit[value=' + Drupal.t('Upload') + ']', $parent).mousedown();
          }
        }, 100);
      });
    }
  };

  Drupal.behaviors.autoUploadMulptipleImages = {
    attach: function(context, settings) {
      $('.multiple-images-upload input.form-submit[value=' + Drupal.t('Upload') + ']', context).hide();
      $('.multiple-images-upload input.form-file', context).change(function() {
        var $parent = $(this).closest('.multiple-images-upload');
        //setTimeout to allow for validation
        //would prefer an event, but there isn't one
        setTimeout(function() {
          if(!$('.error', $parent).length) {
            $('input.form-submit[value=' + Drupal.t('Upload') + ']', $parent).mousedown();
          }
        }, 100);
      });
    }
  };


})(jQuery);
