  Drupal.behaviors.audioPlayer = {
    attach: function (context, settings) {
      let audioSource = $('.m-portlet__audio audio'),
        audioPlay = $('.m-portlet__audio-button'),
        audioPause = $('.m-portlet__audio-pause'),
        audioMute = $('.m-portlet__audio-volume'),
        audioProgress = $('.m-portlet__audio-progress')[0],
        audioPlayhead,
        audioDuration;

      function playOrPause() {
        if (audioSource[0].paused) {
          audioSource[0].play();
          audioPlay.removeClass('m-portlet__audio-play').addClass('m-portlet__audio-pause');
        } else {
          audioSource[0].pause();
          audioPlay.removeClass('m-portlet__audio-pause').addClass('m-portlet__audio-play');
        }
      }

      function muteUnmute() {
        audioSource[0].muted = !audioSource[0].muted;
        if (audioSource[0].muted) {
          audioMute.removeClass('m-portlet__audio-unmuted').addClass('m-portlet__audio-muted')
        } else {
          audioMute.removeClass('m-portlet__audio-muted').addClass('m-portlet__audio-unmuted')
        }
      }

      function updateProgressBar() {
        let progressPercentage = audioSource[0].currentTime / audioSource[0].duration;
        audioProgress.value = progressPercentage || 0;
      }

      function updateTime() {
        audioDuration = audioSource[0].duration || 0;
        audioPlayhead = audioSource[0].currentTime || 0;

        let durationMinutes = parseInt(audioDuration / 60, 10);
        let durationSeconds = parseInt(audioDuration % 60);

        let currentTimeMinutes = parseInt(audioPlayhead / 60, 10);
        let currentTimeSeconds = parseInt(audioPlayhead % 60);

        if (currentTimeMinutes < 10) {
          currentTimeMinutes = '0' + String(currentTimeMinutes);
        }
        if (currentTimeSeconds < 10) {
          currentTimeSeconds = '0' + String(currentTimeSeconds);
        }

        $('.m-portlet__audio-duration').text(durationMinutes + ':' + durationSeconds);
        $('.m-portlet__audio-playhead').text(currentTimeMinutes + ':' + currentTimeSeconds);
      }

      audioPlay.click(playOrPause);
      audioPause.click(playOrPause);
      audioMute.click(muteUnmute);

      if (audioSource.length) {
        setInterval(updateProgressBar, 10);
        setInterval(updateTime, 10);
      }
    }
  };

