  Drupal.behaviors.sessionsDatesFilter = {
    attach: function (context, settings) {
      $('.link-with-confirmation').once(function() {
        $(this).click(function(e) {
          if (!confirm(Drupal.t('Sure?'))) {
            e.preventDefault();
          }
        })
      });

      let events_list = $('.section__events .card__wrap .views-row');
      if (events_list.length && Drupal.settings.filterDate) {
        const events = Drupal.settings.events;
        const lang = Drupal.settings.lang;
        const timezone = Drupal.settings.default_tz;
        const filterDate = Drupal.settings.filterDate;
        window.currentDate = window.currentDate || filterDate && moment(filterDate).tz(timezone).locale(lang);

        function hideEventsList() {
          events_list.each(function () {
            $(this).hide();
          });
        }

        function eventsFilter(events) {
          $('.section__date-filters-title').html('<span>' + currentDate.format('DD MMMM') + '</span>' + ', ' + currentDate.format("dddd"));
          const startDay = currentDate.clone().set({hour:0,minute:0,second:0,millisecond:0}).unix();
          const endDay = currentDate.clone().set({hour:23,minute:59,second:59,millisecond:999}).unix();
          hideEventsList();
          $.each(events, function (nid, event) {
            $.each(event.hours, function (index, hour) {
              if (eventInRange(startDay, endDay, hour.start, hour.end)) {
                $('.event-' + nid).show();
              }
            });
          });
        }

        function eventInRange(start, end, eventStart, eventEnd) {
          return ((eventStart >= start && eventStart <= end) ||
            (eventEnd >= start && eventEnd <= end) ||
            (eventStart <= start && eventEnd >= end));
        }

        $('.filters-control__btn').click(function () {
          if (!$(this).hasClass('filters-control__btn--disabled')) {
            const rangeMin = Drupal.settings.range.min;
            const rangeMax = Drupal.settings.range.max;
            if ($(this).hasClass('filters-control__left')) {
              currentDate.add(-1, 'd');
              if (currentDate.clone().set({hour:0,minute:0,second:0,millisecond:0}).unix() <= rangeMin) {
                $(this).addClass('filters-control__btn--disabled');
              }
              if (currentDate.clone().set({hour:23,minute:59,second:59,millisecond:999}).unix() < rangeMax) {
                $(this).siblings('.filters-control__right').removeClass('filters-control__btn--disabled');
              }
            }
            else {
              currentDate.add(1, 'd');
              if (currentDate.clone().set({hour:0,minute:0,second:0,millisecond:0}).unix() > rangeMin) {
                $(this).siblings('.filters-control__left').removeClass('filters-control__btn--disabled');
              }
              if (currentDate.clone().set({hour:23,minute:59,second:59,millisecond:999}).unix() >= rangeMax) {
                $(this).addClass('filters-control__btn--disabled');
              }
            }
            eventsFilter(events);
          }
        });

        eventsFilter(events);
      }
    }
  };

