  Drupal.behaviors.dateProcessor = {
    attach: (context) => {
      const calendarIcon = '<i class="la la-calendar-o"></i>';
      const clockIcon = '<i class="la la-clock-o"></i>';

      let isUserTimezone = Drupal.settings.activeTimezone === "user";

      $('span.c-dates', context).once(function() {
        let $this = $(this);

        let from = $this.data('from');
        let to = $this.data('to');
        let allDay = $this.data('allDay');
        let calendar = $this.data('icon-hidden') ? '' : calendarIcon;
        let clock = $this.data('icon-hidden') ? '' : clockIcon;

        if (!from) {
          $this.closest('div').remove();
        }

        let endDateShort;

        let startMoment = moment(from * 1000);
        if (!isUserTimezone) {
          startMoment = startMoment.tz(Drupal.settings.timezone);
        }
        let startDateShort = startMoment.format("DD MM YY")
        let endMoment;

        if (to) {
          endMoment = moment(to * 1000);
          if (!isUserTimezone) {
            endMoment = endMoment.tz(Drupal.settings.timezone);
          }
          endDateShort = endMoment.format("DD MM YY");
        }

        let sameDate = !endDateShort || startDateShort === endDateShort;

        let dateStart = startMoment.format('DD ') + Drupal.t(startMoment.format('MMMM')) + startMoment.format(' YYYY');

        let timeStart = startMoment.format('HH:mm');
        let timeEnd;
        let dateEnd;
        if (endMoment) {
          if (!sameDate) {
            dateEnd = endMoment.format('DD ') + Drupal.t(endMoment.format('MMMM')) + endMoment.format(' YYYY');
          }

          timeEnd = endMoment.format('HH:mm');
          if (timeEnd === timeStart && sameDate) {
            timeEnd = null;
          }
        }

        let output = '';
        if (!sameDate) {
          if (allDay) {
            output = `<div class="date-block">${calendar} ${dateStart}</div><div>-</div><div class="date-block">${dateEnd}</div>`;
          }
          else {
            output = `<div class="date-block">${calendar} ${dateStart}</div><div class="date-block">${clock} ${timeStart}</div><div>-</div><div class="date-block">${calendar} ${dateEnd}</div><div class="date-block">${clock} ${timeEnd}</div>`
          }
        }
        else {
          if (allDay) {
            output = `<div class="date-block">${calendar} ${dateStart}</div>`
          }
          else  {
            if (timeEnd) {
              output = `<div class="date-block">${calendar} ${dateStart}</div><div class="date-block">${clock} ${timeStart}  ${timeEnd}</div>`
            }
            else {
              output = `<div class="date-block">${calendar} ${dateStart}</div><div class="date-block">${clock} ${timeStart}</div>`
            }
          }
        }

        $this.replaceWith(output);
      });
    }
  }

