(($) => {
  window.Campuz = window.Campuz || {};

  Drupal.behaviors.campuzSocket = {
    attach: () => {
      Campuz.sockets.initialize()
    },
    weight: -10
  }
  Campuz.sockets = Campuz.sockets || {
    initialized: false,
    events: {}
  };

  Campuz.sockets.initialize = function () {
    if (!this.initialized) {
      this.initialized = true;
      if (typeof Drupal.settings.campuz !== "undefined" && Drupal.settings.campuz.sockets.connectionUrl) {
        this.io = io(
          Drupal.settings.campuz.sockets.connectionUrl,
          {
            'query': {
              'userId': Drupal.settings.campuz.sockets.userId,
              'Authorization': 'Bearer ' + Drupal.settings.campuz.sockets.token,
              'language': Drupal.settings.lang,
              'url': window.location.href
            }
          }
        )
          .on("initialized", () => {
            let rooms = this.getCurrentRoom();
            if (rooms) {
              this.io.emit('enter_rooms', {"rooms": rooms, "bounce": this.bounce()});
              this.io.emit('drupal_settings', {
                'unreadChannels': Drupal.settings.campuz.sockets.unread_channels ? Drupal.settings.campuz.sockets.unread_channels : [],
                "bounce": this.bounce()
              });
            }
          })
          .on("frontEvent", data => {
            let event = data.event;

            if (event && typeof Campuz.sockets.events[event] !== 'undefined') {
              Campuz.sockets.events[event](data);
            }
          })
          .on("connection_error", reason => function() {
            console.error(reason.message.en);
            Campuz.sockets.io.io.autoConnect = false;
          });
      }
    }
  };

  Campuz.sockets.bounce = function () {
    return Math.random().toString(36).substring(3, 5)
      + Math.random().toString(36).substring(4, 8);
  };

  Campuz.sockets.getCurrentRoom = function() {
    return Drupal.settings.campuz.sockets.rooms || [];
  };

  // Front events
  Campuz.sockets.events.refresh = function() {
    window.location.reload();
  };

  Campuz.sockets.events.userLogin = function (eventData) {
    let userId = Drupal.settings.campuz.sockets.userId;
    let currentPath = window.location.pathname;
    if (userId != 0 && userId == eventData.userId
      && currentPath.search('user/login') === -1) {
      window.location.reload();
    }
  };

})(jQuery);
;
($ => {
  Drupal.behaviors.notifications = {
    attach: () => {
      $('body').once('notifications', () => {

        if (typeof Campuz !== 'undefined' && Campuz.sockets && Campuz.sockets.io) {
          Campuz.sockets.io.on('notification', (data) => {
            if (data.notificationData.type == 'event') {
              const message = data.notificationData.content.message[Drupal.settings.lang] ||
                data.notificationData.content.message[Object.keys(data.content.message)[0]];

              const isTablet = window.matchMedia('(max-width: 1024px)').matches;
              const isMobile = window.matchMedia('(max-width: 576px)').matches;

              $.notify({
                message
              }, {
                type: 'broadcast-notification',
                position: 'fixed',
                placement: {
                  from: "bottom",
                  align: "left"
                },
                offset: {
                  y: (isMobile) ? 0 : (isTablet) ? 16 : 71,
                },
                allow_dismiss: true,
                newest_on_top: false,
                delay: 30000,
                z_index: 11000,
              });
            }
          });
        }
      });
    }
  };
})(jQuery);
;
($ => {
  Drupal.behaviors.languageSwitcherSelectList = {
    attach: (context, settings) => {
      $(document).ready(function () {
        function switcherLang(mainWrap) {
          let activeLang = $('.lang-switcher .dropdown-toggle');
          let listLangKey = $(mainWrap).find('select option');
          let listLangFlags = Drupal.settings.campuz.languages;
          let parentBlock = mainWrap;

          if (mainWrap === '#m_header_topbar' && window.matchMedia('(max-width: 1024px)').matches) {
            var switchLangWrap = document.createElement('li');
            var switchLangBtn  = $('#m_header_topbar > .lang-switcher');
            parentBlock = $('.m-menu__nav--dropdown-submenu-arrow');

            $(switchLangWrap).append(switchLangBtn);
            parentBlock.append(switchLangWrap);
          }

          listLangKey.each((index, item) => {
            let icon = listLangFlags[$(item).val()].svg;

            if ($(item).attr('selected') && $(activeLang).find('svg').length === 0) {
              activeLang.find('.filter-option').prepend(icon);
            }
          });

          if (activeLang.length) {
            activeLang.on('click', function () {
              let listLangItem = $(parentBlock).find('.dropdown-menu.inner li');

              listLangKey.each((index, item) => {
                if ($(listLangItem[index]).find('svg').length === 0) {
                  let icon = listLangFlags[$(item).val()].svg;
                  $(listLangItem[index]).find('a').prepend(icon);

                  $(listLangItem[index]).on('click', function () {
                    let newIco = $(this).find('svg');
                    activeLang.find('.filter-option svg').replaceWith(newIco)
                    window.location.href = $(item).attr('data-href');
                  })
                }
              })
            })
          }
        }

        switcherLang('#m_header_topbar');
        switcherLang('.language-switcher--js');
      })
    }
  };
})(jQuery);
;
